import React, { useState } from 'react';
import './Reviews.css';

const Reviews = () => {
  const [currentReview, setCurrentReview] = useState(0);

  const reviews = [
    {
      id: 1,
      name: 'Michael Chen',
      position: 'Graphic Designer',
      text: 'This platform completely transformed my freelance career! I was struggling to find consistent work, but now I\'m getting steady projects from clients worldwide.',
      image: 'data:image/jpeg;base64,'  // Use a working placeholder
    },
    {
      id: 2,
      name: 'Sarah Rodriguez',
      position: 'Small Business Owner',
      text: 'As a startup founder, finding the right talent used to be a nightmare. This freelance platform changed everything. I\'ve hired incredible developers, designers, and marketing experts.',
      image: 'data:image/jpeg;base64,'
    },
    {
      id: 3,
      name: 'Alex Thompson',
      position: 'Web Developer',
      text: 'I was skeptical about online freelancing platforms, but this site has been a game-changer. The secure payment system, transparent job postings, and ability to showcase my portfolio have helped me build a thriving freelance business.',
      image: 'data:image/jpeg;base64,'
    }
  ];

  const handlePrevReview = () => {
    setCurrentReview((prev) => (prev === 0 ? reviews.length - 1 : prev - 1));
  };

  const handleNextReview = () => {
    setCurrentReview((prev) => (prev === reviews.length - 1 ? 0 : prev + 1));
  };

  const renderStars = () => {
    return Array(5).fill(0).map((_, index) => (
      <span key={index} className="star" style = {{color: '#FFD700'}}>â˜…</span>
    ));
  };

  return (
    <div className="reviews-container">
      <h2 className="reviews-title">What <span className="highlight">Professionals Say</span></h2>

      <div className="reviews-slider">
        <button className="slider-btn prev-btn" onClick={handlePrevReview}>
          &#10094;
        </button>

        <div className="review-card">
          <div className="stars">
            {renderStars()}
          </div>

          <p className="review-text">{reviews[currentReview].text}</p>

          <div className="reviewer-info">
            <img src={reviews[currentReview].image} alt={reviews[currentReview].name} className="reviewer-image" />
            <div className="reviewer-details">
              <h3 className="reviewer-name">{reviews[currentReview].name}</h3>
              <p className="reviewer-position">{reviews[currentReview].position}</p>
            </div>
          </div>
        </div>

        <button className="slider-btn next-btn" onClick={handleNextReview}>
          &#10095;
        </button>
      </div>

      {/* Cherry Blossom Effect */}
      <div className="cherry-blossoms">
        {[...Array(5)].map((_, index) => (
          <div key={index} className="blossom" style={{ left: `${Math.random() * 100}%`, animationDelay: `${Math.random() * 3}s` }} />
        ))}
      </div>
    </div>
  );
};

export default Reviews;
